package com.lucifer.razerwidget.imageviewactivity;import com.lucifer.razerlib.imageview.photosortimage.PhotoSortView;import com.lucifer.razerwidget.R;import android.app.Activity;import android.os.Bundle;import android.view.KeyEvent;public class PhotoSortActivity extends Activity{	PhotoSortView photoSorter;	@Override	public void onCreate(Bundle savedInstanceState)	{		super.onCreate(savedInstanceState);				photoSorter = new PhotoSortView(this);		photoSorter.setImageList(new int[]{R.drawable.ic_launcher, R.drawable.ic_launcher, R.drawable.ic_launcher,				R.drawable.ic_launcher, R.drawable.ic_launcher });		photoSorter.init(this);				setContentView(photoSorter);			}	@Override	protected void onResume()	{		super.onResume();		photoSorter.loadImages(this);	}	@Override	protected void onPause()	{		super.onPause();		photoSorter.unloadImages();	}	@Override	public boolean onKeyDown(int keyCode, KeyEvent event)	{		if (keyCode == KeyEvent.KEYCODE_DPAD_CENTER)		{			photoSorter.trackballClicked();			return true;		}		return super.onKeyDown(keyCode, event);	}}